import '../css/GerirPerfis.css';
import React, { useState, useEffect } from 'react';

export default function GerirPerfis() {
	const [users, setUsers] = useState([]);
	const [selectedUser, setSelectedUser] = useState(null);
	const [editedUser, setEditedUser] = useState(null);

	useEffect(() => {
		async function fetchUsers() {
			try {
				const response = await fetch(
					'http://localhost:8080/user/getAllUsers',
					{
						method: 'GET',
						headers: {
							'Content-Type': 'application/json',
						},
					}
				);
				if (!response.ok) {
					throw new Error('Failed to fetch users data');
				}
				const usersData = await response.json();
				setUsers(usersData);
			} catch (error) {
				console.error(
					'An error occurred while fetching users data: ',
					error
				);
			}
		}
		fetchUsers();
	}, []);

	/*  addUser.addEventListener('click', async () => {
            const fieldsToSubmit = [];
            for (const input of inputFields) {
                if (input.value.trim() === '') {
                    alert(
                        'Por favor, preencha todos os espaços obrigatórios para adicionar um utilizador'
                    );
                    return; // Não continue o loop se houver um campo em branco
                }
                fieldsToSubmit.push(input.value);
            }
            try {
                const fullName = document.getElementById('fullName').value;
                const birthday = document.getElementById('birthday').value;
                const nif = document.getElementById('nif').value;
                const userAddress = document.getElementById('userAddress').value;
                const role = document.getElementById('role').value;
                const insuranceName =
                    document.getElementById('insuranceName').value;
                const insurancePolicy =
                    document.getElementById('insurancePolicy').value;
                const phone = document.getElementById('phone').value;
                const username = document.getElementById('username').value;
                const response = await fetch(
                    'http://localhost:8080/user/createUser',
                    {
                        method: 'POST',
                        headers: {
                            'Content-Type': 'application/json',
                        },
                        body: JSON.stringify({
                            fullName,
                            birthday,
                            nif,
                            userAddress,
                            role,
                            insuranceName,
                            insurancePolicy,
                            phone,
                            username,
                        }),
                    }
                );
                /* 
                const data = await response.json();
                console.log(data); */
	/* if (response.ok) {
                    for (const input of inputFields) {
                        input.value = ''; // Limpar os campos de entrada
                    }
                    window.location.reload();
                } else {
                    alert('Erro a adicionar utilizador');
                }
            } catch (err) {
                console.error('Erro na solicitação', err);
            }
        }); //Lets fill la table:

        tbody.innerHTML = '';

        try {
            const response = await fetch('http://localhost:8080/user/allUsers', {
                method: 'GET',
                headers: {
                    'Content-Type': 'application/json',
                },
            });
            if (!response.ok) {
                throw new Error('Failed to fetch data');
            }
            const data = await response.json();
            console.log(data);

            // Use map and template literals to generate table rows
            const rowsHTML = data.allUsers
                .map(
                    (user) => `
                    <tr  data-username="${user.username}">
                        <td>${user.username}</td>
                        <td>${user.fullName}</td>
                        <td>${user.nif}</td>
                        <td>${user.phone}</td>
                        <td>${user.insurance.policy}</td>
                    </tr>
                `
                )
                .join('');

            tbody.innerHTML = rowsHTML;
        } catch (error) {
            console.error('An error occurred: ', error); 
        } */
	/*     
        //select them lines on click:
        for (const line of lines) {
            line.addEventListener('click', async () => {
                // Remove 'selected' class from all rows
                for (const noSelect of lines) {
                    noSelect.classList.remove('selected');
                }

                // Add 'selected' class to the clicked row
                line.classList.add('selected');

                // Retrieve a unique identifier for the selected row, e.g., an ID
                const rowUsername = line.getAttribute('data-username'); // Assuming you have a data-id attribute on each row */
	const handleDeleteUser = async (usernameToDelete) => {
		try {
			const response = await fetch(
				`http://localhost:8080/user/deleteUser?username=${usernameToDelete}`,
				{
					method: 'DELETE',
					headers: {
						'Content-Type': 'application/json',
					},
				}
			);

			if (!response.ok) {
				throw new Error('Failed to delete user');
			}

			// Remove the deleted user from the list
			setUsers(
				users.filter((user) => user.username !== usernameToDelete)
			);
		} catch (error) {
			console.error('An error occurred while deleting user: ', error);
		}
	};

	const handleEditUser = async () => {
		const usernameToEdit = editedUser.username;

		try {
			const response = await fetch(
				`http://localhost:8080/user/updateUser?username=${usernameToEdit}`,
				{
					method: 'PUT',
					headers: {
						'Content-Type': 'application/json',
					},
					body: JSON.stringify(editedUser),
				}
			);
			if (!response.ok) {
				throw new Error('Failed to update user');
			}

			// Update the edited user in the list
			setUsers(
				users.map((user) =>
					user.username === usernameToEdit ? editedUser : user
				)
			);

			// Clear the selected and edited users
			setSelectedUser(null);
			setEditedUser(null);
		} catch (error) {
			console.error('An error occurred while editing user: ', error);
		}
	};

	return (
		<div className='GerirPerfis '>
			<div className='container backgroundSec'>
				<h1>Gerir Perfis</h1>
				<div className='row'>
					<div className='col-12 col-lg-6 table-responsive'>
						<table>
							<thead>
								<tr>
									<th>Username</th>
									<th>Full Name</th>
									<th>NIF</th>
									<th>Phone</th>
									<th>Insurance Policy</th>
								</tr>
							</thead>
							<tbody>
								{users.map((user) => (
									<tr key={user.username}>
										<td>{user.username}</td>
										<td>{user.fullName}</td>
										<td>{user.nif}</td>
										<td>{user.phone}</td>
										<td>{user.insurance.policy}</td>
										<td>
											<button
												onClick={() =>
													setSelectedUser(user)
												}
											>
												Edit
											</button>
											<button
												onClick={() =>
													handleDeleteUser(
														user.username
													)
												}
											>
												Delete
											</button>
										</td>
									</tr>
								))}
							</tbody>
						</table>

						{selectedUser && (
							<div>
								<form>
									<label>
										Username:
										<input
											type='text'
											value={selectedUser.username}
											readOnly
										/>
									</label>
									<label>
										Full Name:
										<input
											type='text'
											value={
												editedUser?.fullName ??
												selectedUser.fullName
											}
											onChange={(event) =>
												setEditedUser({
													...editedUser,
													fullName:
														event.target.value,
												})
											}
										/>
									</label>
									<label>
										NIF:
										<input
											type='text'
											value={
												editedUser?.nif ??
												selectedUser.nif
											}
											onChange={(event) =>
												setEditedUser({
													...editedUser,
													nif: event.target.value,
												})
											}
										/>
									</label>
									<label>
										User Address:
										<input
											type='text'
											value={
												editedUser?.userAddress ??
												selectedUser.userAddress
											}
											onChange={(event) =>
												setEditedUser({
													...editedUser,
													userAddress:
														event.target.value,
												})
											}
										/>
									</label>
									<label>
										Role:
										<input
											type='text'
											value={
												editedUser?.role ??
												selectedUser.role
											}
											onChange={(event) =>
												setEditedUser({
													...editedUser,
													role: event.target.value,
												})
											}
										/>
									</label>
									<label>
										Insurance Name:
										<input
											type='text'
											value={
												editedUser?.insurance?.name ??
												selectedUser.insurance.name
											}
											onChange={(event) =>
												setEditedUser({
													...editedUser,
													insurance: {
														...editedUser?.insurance,
														name: event.target
															.value,
													},
												})
											}
										/>
									</label>
									<label>
										Insurance Policy:
										<input
											type='text'
											value={
												editedUser?.insurance?.policy ??
												selectedUser.insurance.policy
											}
											onChange={(event) =>
												setEditedUser({
													...editedUser,
													insurance: {
														...editedUser?.insurance,
														policy: event.target
															.value,
													},
												})
											}
										/>
									</label>
									<label>
										Phone:
										<input
											type='text'
											value={
												editedUser?.phone ??
												selectedUser.phone
											}
											onChange={(event) =>
												setEditedUser({
													...editedUser,
													phone: event.target.value,
												})
											}
										/>
									</label>
									<button
										type='button'
										onClick={handleEditUser}
									>
										Save
									</button>
									<button
										type='button'
										onClick={() => setSelectedUser(null)}
									>
										Cancel
									</button>
								</form>
							</div>
						)}
					</div>
				</div>
			</div>
		</div>
	);
}
