import { useState } from 'react';
import './Modal.css';

export default function Modal() {
	const [selectedImage, setSelectedImage] = useState(null);

	const handleImageClick = (img) => {
		setSelectedImage(img);
	};

	const renderForm = () => {
		if (selectedImage) {
			return (
				<div className='row flex-lg-wrap botaoPositioning'>
					<form action='/action_page.php' method='post'>
						<label className='passWordEdit' htmlFor='lname'>
							PASSWORD:
						</label>
						<input type='password' id='lname' name='lname' />
						<br />
						<div className='d-flex bd-highlight mb-3'>
							<div className='p-2 bd-highlight'>
								<div>
									<input
										type='submit'
										className='submitButton'
										id='entradaConta'
										name='entradaConta'
										value='ENTRADA'
									/>
								</div>
							</div>
							<div className='p-2 bd-highlight'>
								<div>
									<input
										type='submit'
										className='submitButton'
										id='saidaConta'
										name='saidaConta'
										value='SAIDA'
									/>
								</div>
							</div>
							<div className='ms-auto p-2 bd-highlight'>
								<div>
									<input
										type='submit'
										className='submitButton'
										id='loginConta'
										name='loginConta'
										value='LOGIN'
									/>
								</div>
							</div>
						</div>
					</form>
				</div>
			);
		}
		return null;
	};
	return (
		<div className='Modal'>
			<div
				className='modal fade'
				id='exampleModal'
				tabIndex='-1'
				aria-labelledby='exampleModalLabel'
				aria-hidden='true'
			>
				<div className='modal-dialog modal-dialog-centered '>
					<div className='modal-content'>
						<div className='modal-body imageContainer'>
							<div className='row d-lg-flex'>
								{images.map((img, index) => (
									<div
										className='col-lg text-center'
										key={index}
									>
										<img
											className={`rounded-circle imgag ${
												selectedImage === img
													? 'selected-img'
													: ''
											}`}
											alt={`avatar${index}`}
											src={`../../Images/user${
												index + 1
											}.png`}
											onClick={() =>
												handleImageClick(img)
											}
										/>
										<div className='mb-2'></div>
										<span className='text-lg-center editNames'>
											User Name
										</span>
									</div>
								))}
							</div>
						</div>
						<div className='outputContainer'>{renderForm()}</div>
					</div>
				</div>
			</div>
		</div>
	);
}
