import React, { useState, useEffect } from 'react';
import '../css/GerirPerfis.css';

export default function GerirPerfis() {
	const [users, setUsers] = useState([]);
	const [selectedUser, setSelectedUser] = useState(null);
	const [editedUser, setEditedUser] = useState({
		fullName: '',
		nif: '',
		userAddress: '',
		role: '',
		insurance: {
			name: '',
			policy: '',
		},
		phone: '',
		username: '',
	});

	useEffect(() => {
		async function fetchUsers() {
			try {
				const response = await fetch(
					'http://localhost:8080/user/getAllUsers',
					{
						method: 'GET',
						headers: {
							'Content-Type': 'application/json',
						},
					}
				);
				if (!response.ok) {
					throw new Error('Failed to fetch users data');
				}
				const usersData = await response.json();
				setUsers(usersData);
			} catch (error) {
				console.error(
					'An error occurred while fetching users data: ',
					error
				);
			}
		}
		fetchUsers();
	}, []);

	const handleDeleteUser = async (usernameToDelete) => {
		try {
			const response = await fetch(
				`http://localhost:8080/user/deleteUser?username=${usernameToDelete}`,
				{
					method: 'DELETE',
					headers: {
						'Content-Type': 'application/json',
					},
				}
			);

			if (!response.ok) {
				throw new Error('Failed to delete user');
			}

			// Remove the deleted user from the list
			setUsers(
				users.filter((user) => user.username !== usernameToDelete)
			);
			setSelectedUser(null);
		} catch (error) {
			console.error('An error occurred while deleting user: ', error);
		}
	};

	const handleEditUser = async () => {
		const usernameToEdit = editedUser.username;

		try {
			const response = await fetch(
				`http://localhost:8080/user/updateUser?username=${usernameToEdit}`,
				{
					method: 'PUT',
					headers: {
						'Content-Type': 'application/json',
					},
					body: JSON.stringify(editedUser),
				}
			);
			if (!response.ok) {
				throw new Error('Failed to update user');
			}

			// Update the edited user in the list
			setUsers(
				users.map((user) =>
					user.username === usernameToEdit ? editedUser : user
				)
			);

			// Clear the selected and edited users
			setEditedUser({
				fullName: '',
				nif: '',
				userAddress: '',
				role: '',
				insurance: {
					name: '',
					policy: '',
				},
				phone: '',
				username: '',
			});
			setSelectedUser(null);
		} catch (error) {
			console.error('An error occurred while editing user: ', error);
		}
	};

	return (
		<div className='GerirPerfis'>
			<div className='container backgroundSec'>
				<h1>Gerir Perfis</h1>
				<div className='row'>
					<div className='col-12 col-lg-6 table-responsive'>
						<table>
							<thead>
								<tr>
									<th>Username</th>
									<th>Full Name</th>
									<th>NIF</th>
									<th>Phone</th>
									<th>Insurance Policy</th>
								</tr>
							</thead>
							<tbody>
								{users.map((user) => (
									<tr key={user.username}>
										<td>{user.username}</td>
										<td>{user.fullName}</td>
										<td>{user.nif}</td>
										<td>{user.phone}</td>
										<td>{user.insurance.policy}</td>
										<td>
											<button
												onClick={() =>
													setSelectedUser(user)
												}
											>
												Edit
											</button>
											<button
												onClick={() =>
													handleDeleteUser(
														user.username
													)
												}
											>
												Delete
											</button>
										</td>
									</tr>
								))}
							</tbody>
						</table>

						{selectedUser && (
							<div>
								<form>
									<label>
										Username:
										<input
											type='text'
											value={selectedUser.username}
											readOnly
										/>
									</label>
									<label>
										Full Name:
										<input
											type='text'
											value={editedUser.fullName}
											onChange={(event) =>
												setEditedUser({
													...editedUser,
													fullName:
														event.target.value,
												})
											}
										/>
									</label>
									<label>
										NIF:
										<input
											type='text'
											value={editedUser.nif}
											onChange={(event) =>
												setEditedUser({
													...editedUser,
													nif: event.target.value,
												})
											}
										/>
									</label>
									<label>
										User Address:
										<input
											type='text'
											value={editedUser.userAddress}
											onChange={(event) =>
												setEditedUser({
													...editedUser,
													userAddress:
														event.target.value,
												})
											}
										/>
									</label>
									<label>
										Role:
										<input
											type='text'
											value={editedUser.role}
											onChange={(event) =>
												setEditedUser({
													...editedUser,
													role: event.target.value,
												})
											}
										/>
									</label>
									<label>
										Insurance Name:
										<input
											type='text'
											value={editedUser.insurance.name}
											onChange={(event) =>
												setEditedUser({
													...editedUser,
													insurance: {
														...editedUser.insurance,
														name: event.target
															.value,
													},
												})
											}
										/>
									</label>
									<label>
										Insurance Policy:
										<input
											type='text'
											value={editedUser.insurance.policy}
											onChange={(event) =>
												setEditedUser({
													...editedUser,
													insurance: {
														...editedUser.insurance,
														policy: event.target
															.value,
													},
												})
											}
										/>
									</label>
									<label>
										Phone:
										<input
											type='text'
											value={editedUser.phone}
											onChange={(event) =>
												setEditedUser({
													...editedUser,
													phone: event.target.value,
												})
											}
										/>
									</label>
									<button
										type='button'
										onClick={handleEditUser}
									>
										Save
									</button>
									<button
										type='button'
										onClick={() => setSelectedUser(null)}
									>
										Cancel
									</button>
								</form>
							</div>
						)}
					</div>
				</div>
			</div>
		</div>
	);
}
