									<th scope='col'>Nome</th>
									<th scope='col'>NIF</th>
									<th scope='col'>Contacto</th>
									<th scope='col'>Apólice</th>
								</tr>
							</thead>
							<tbody>
								{users.map((user) => (
									<tr key={user.username}>
										<td>{user.username}</td>
										<td>{user.fullName}</td>
										<td>{user.nif}</td>
										<td>{user.phone}</td>
										<td>{user.insurance?.policy}</td>
									</tr>
								))}
